mapper.insert:
INSERT INTO 
member (
		email
		, pw
		, user_name
		, auth_key
		, auth_valid_date
		)
VALUES( 
	#{email}
    , #{pw}
    , #{user_name}
    , #{auth_key}
    , date_add(date_format(now(), '%Y-%m-%d'), INTERVAL 7 DAY)
)

mapper.select:
SELECT *
  FROM member
 WHERE mem_id = #{mem_id}
   AND auth_key = #{auth_key}
